import React, { useState, useEffect } from 'react';

import './App.css';
import Axios from 'axios';
// import Web3 from 'web3';

import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';

const useStyles = makeStyles({
    root: {
        maxWidth: 345,
    },
    media: {
        height: 140,
    },
});

const ItemsPage = () => {

    const classes = useStyles();

    const [movieReviewList, setReviewList] = useState([])

    useEffect(() => {
        Axios.get('http://localhost:3001/api/get').then((response) => {
            setReviewList(response.data);
        })
    }, [])

    return (
        <div>
            <h1>Welcome to Items Page</h1>

            <Card className={classes.root}>
                <CardActionArea>
                    <CardMedia
                        className={classes.media}
                        image="https://images.pexels.com/photos/3293148/pexels-photo-3293148.jpeg?cs=srgb&dl=pexels-asad-photo-maldives-3293148.jpg&fm=jpg"
                        title="Contemplative Reptile"
                    />
                    <CardContent>
                        <Typography gutterBottom variant="h4" component="h2">
                            Lizard
                        </Typography>
                        <Typography variant="h5" color="textSecondary" component="p">
                            Lizards are a widespread group of squamate reptiles, with over 6,000 species, ranging
                            across all continents except Antarctica
                        </Typography>
                    </CardContent>
                </CardActionArea>
                <CardActions>
                    <Button size="small" color="primary">
                        Share
                    </Button>
                    <Button size="small" color="primary">
                        Learn More
                    </Button>
                </CardActions>
            </Card>

            {movieReviewList.map((val) => {



                return (
                    <div className="card">
                        <h1>{val.movie_name}</h1>
                        <p>{val.movie_review}</p>

                        <img src={'http://localhost:3001/img/' + val.userid} alt="hey" id="img"></img>

                        <button onClick={() => { deleteReview(val.movie_name) }}>Delete</button>
                        <input type="text" id="updateInput" placeholder="Enter new Review" onChange={(e) =>
                            setNewReview(e.target.value)
                        } />
                        <button onClick={() => { updateReview(val.movie_name) }}>Update</button>

                    </div>



                )


            })}
        </div>

    );
};

export default ItemsPage;
