import { Container } from "@material-ui/core";
import Box from "@material-ui/core/Box";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import Grid from '@material-ui/core/Grid';
import { withStyles } from "@material-ui/styles";
import axios from "axios";
import React from "react";
import AddDoctorForm from "../addDoctor/AddDoctorForm";
import AddPatientForm from "../addPatient/AddPatientForm";
import DoctorList from "../doctorList/DoctorList";
import PatientList from "../patientList/PatientList";
import SearchBoxPatients from "../searchBoxPatients/SearchBoxPatients";
import { openSnackBar } from "../snackBar/SnackBar";

const styles = (theme) => ({
  box: {
    margin: theme.spacing(1),
  },
});

class Patients extends React.Component {
  constructor(props) {
    super(props);
    console.log(this);
    this.state = {
      patients: [],
      filterPatient: "",
      addingPatient: false,
      doctors: [],
      filterDoctor: "",
      addingDoctor: false,
    };
    this.handlePatientFilterChange = this.handlePatientFilterChange.bind(this);
    this.handleDoctorFilterChange = this.handleDoctorFilterChange.bind(this);
    this.toogleAddingPatientStatus = this.toogleAddingPatientStatus.bind(this);
    this.toogleAddingDoctorStatus = this.toogleAddingDoctorStatus.bind(this);
    this.addPatient = this.addPatient.bind(this);
    this.addDoctor = this.addDoctor.bind(this);
  }

  componentDidMount() {
    axios
      .get(`http://localhost:3001/patients/`, {
        headers: {
          "x-access-token": localStorage.getItem("jwtToken"),
        },
      })
      .then(
        (result) => {
          console.log(result.data);
          this.setState({
            patients: result.data,
            filterPatient: this.state.filterPatient,
            addingPatient: this.state.addingPatient,
            doctors: this.state.doctors,
            filterDoctor: this.state.filterDoctor,
            addingDoctor: this.state.addingDoctor,
          });
        },
        (error) => {
          openSnackBar({ message: "Authentication Error", type: "error" });
        }
      );
    axios
      .get(`http://localhost:3001/doctors/`, {
        headers: {
          "x-access-token": localStorage.getItem("jwtToken"),
        },
      })
      .then(
        (result) => {
          console.log(result.data);
          this.setState({
            patients: this.state.patients,
            filterPatient: this.state.filterPatient,
            addingPatient: this.state.addingPatient,
            doctors: result.data,
            filterDoctor: this.state.filterDoctor,
            addingDoctor: this.state.addingDoctor,
          });
        },
        (error) => {
          openSnackBar({ message: "Authentication Error", type: "error" });
        }
      );
  }

  render() {

    return (

      // <div>

      <Grid
        container
        spacing={0}
        direction="column"
        alignItems="center"
        justify="center"
        style={{ minHeight: '100vh' }}
      >

        <Grid item xs={3}>

          <Button
            type="submit"
            className={"MuiButton-Full"}
            variant="contained"
            size="large"
            color="primary"
            style={{margin : '15px', height : '70px', width : '500px', fontSize : '30px'}}
          >Create Token
          </Button>

          <Button
            type="submit"
            className={"MuiButton-Full"}
            variant="contained"
            size="large"
            color="primary"
            style={{margin : '15px', height : '70px', width : '500px', fontSize : '30px'}}
          >Buy Token
          </Button>

          <Button
            type="submit"
            className={"MuiButton-Full"}
            variant="contained"
            size="large"
            color="primary"
            style={{margin : '15px', height : '70px', width : '500px', fontSize : '30px'}}
          >Transfer Token</Button>

          <Button
            type="submit"
            className={"MuiButton-Full"}
            variant="contained"
            size="large"
            color="primary"
            style={{margin : '15px', height : '70px', width : '500px', fontSize : '30px'}}
          >My Items</Button>

        </Grid>

      </Grid>
      // </div>
    );
  }
}
export default withStyles(styles, { withTheme: true })(Patients);
