import { withStyles } from "@material-ui/core";
import Box from "@material-ui/core/Box";
import Button from "@material-ui/core/Button";
import Card from "@material-ui/core/Card";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import Typography from "@material-ui/core/Typography";
import React from "react";
import PatientExpandedInfo from "./PatientExpandedInfo";

const styles = (theme) => ({
  card: {
    minWidth: 275,
    margin: theme.spacing(1),
  },
});

class PatientCard extends React.Component {
  constructor(props) {
    super(props);
    this.state = { isExpanded: false };
    this.expand = this.expand.bind(this);
    this.shrink = this.shrink.bind(this);
  }

  expand() {
    this.setState({ isExpanded: true });
  }

  shrink() {
    this.setState({ isExpanded: false });
  }

  render() {
    const { classes } = this.props;
    console.log(this.props);
    return (
      <Card className={classes.card}>
        <CardContent>
          <Box mb={1}>
            <Typography color="textSecondary" gutterBottom>
              PID {this.props.PID}
            </Typography>
            <Typography variant="h5" component="h2">
              {this.props.firstName} {this.props.lastName}
            </Typography>
            <Typography color="textSecondary">Age: {this.props.age}</Typography>
            <Typography color="textSecondary">
              Address: {this.props.address}
            </Typography>
          </Box>
          {this.state.isExpanded ? (
            <PatientExpandedInfo id={this.props.Key} onExit={this.shrink} />
          ) : null}
        </CardContent>
        <CardActions>
          {this.state.isExpanded ? (
            <Button size="small" onClick={this.shrink}>
              Shrink
            </Button>
          ) : (
            <Button size="small" onClick={this.expand}>
              Expand
            </Button>
          )}
        </CardActions>
      </Card>
    );
  }
}
export default withStyles(styles, { withTheme: true })(PatientCard);
