import { Container } from "@material-ui/core";
import Box from "@material-ui/core/Box";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import Grid from '@material-ui/core/Grid';
import { withStyles } from "@material-ui/styles";
import axios from "axios";
import React from "react";
import AddDoctorForm from "../addDoctor/AddDoctorForm";
import AddPatientForm from "../addPatient/AddPatientForm";
import DoctorList from "../doctorList/DoctorList";
import PatientList from "../patientList/PatientList";
import SearchBoxPatients from "../searchBoxPatients/SearchBoxPatients";
import { openSnackBar } from "../snackBar/SnackBar";
import { Link } from 'react-router-dom';
import Create from '../create/Create'

const styles = (theme) => ({
  box: {
    margin: theme.spacing(1),
  },
});

class Patients extends React.Component {

  constructor(props) {
    super(props);
    console.log(this);
    this.state = {
      addingDoctor: true,
    };
    // this.toogleAddingPatientStatus = this.toogleAddingPatientStatus.bind(this);
  }

  toogleAddingDoctorStatus() {
    this.setState({ addingDoctor: !this.state.addingDoctor });
  }



  render() {

    let addingPatient;



    return (


      <Grid
        container
        spacing={0}
        direction="row"
      >

        {this.state.addingDoctor ? <button>yoyo</button> : <button>Hello</button>}

        <a href="/create">
          <Button
            type="submit"
            className={"MuiButton-Full"}
            variant="contained"
            size="large"
            color="primary"
            onClick={this.toogleAddingPatientStatus}
            style={{ margin: '15px 15px 15px 100px', height: '70px', width: '400px', fontSize: '30px' }}
          >Create Token
          </Button>

        </a>


        <a href="/buy">
          <Button
            type="submit"
            className={"MuiButton-Full"}
            variant="contained"
            size="large"
            color="primary"
            style={{ margin: '15px', height: '70px', width: '400px', fontSize: '30px' }}
          >Buy Token
          </Button>
        </a>



        <a href="/transfer">
          <Button
            type="submit"
            className={"MuiButton-Full"}
            variant="contained"
            size="large"
            color="primary"
            style={{ margin: '15px', height: '70px', width: '400px', fontSize: '30px' }}
          >Transfer Token
          </Button>
        </a>

        <a href="/items">
          <Button
            type="submit"
            className={"MuiButton-Full"}
            variant="contained"
            size="large"
            color="primary"
            style={{ margin: '15px', height: '70px', width: '400px', fontSize: '30px' }}
          >My Items
          </Button>
        </a>


      </Grid>
    );
  }
}
export default withStyles(styles, { withTheme: true })(Patients);
