import { Container } from "@material-ui/core";
import Box from "@material-ui/core/Box";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import axios from "axios";
import React, { useEffect, useState } from "react";
import AddPatientForm from "../addPatient/AddPatientForm";
import PatientList from "../patientList/PatientList";
import SearchBoxDoctors from "../searchBoxPatients/SearchBoxDoctors";
import SearchBoxPatients from "../searchBoxPatients/SearchBoxPatients";
import { openSnackBar } from "../snackBar/SnackBar";

const styles = (theme) => ({
  box: {
    margin: theme.spacing(1),
  },
});

export default function Patients() {
  const [patients, setPatients] = useState(null);
  const [doctors, setDoctors] = useState(null);
  const [filterDoctor, setFilterDoctor] = useState(null);
  const [filterPatient, setFilterPatient] = useState(null);
  const [addingPatient, setAddingPatient] = useState(false);
  const [addingDoctor, setAddingDoctor] = useState(false);

  useEffect(() => {
    axios
      .get(`http://localhost:3001/patients/`, {
        headers: {
          "x-access-token": localStorage.getItem("jwtToken"),
        },
      })
      .then(
        (result) => {
          console.log(result.data);
          setPatients(result.data);
        },
        (error) => {
          openSnackBar({ message: error.response.data.message, type: "error" });
        }
      );
    axios
      .get(`http://localhost:3001/doctors/`, {
        headers: {
          "x-access-token": localStorage.getItem("jwtToken"),
        },
      })
      .then(
        (result) => {
          console.log(result.data);
          setDoctors(result.data);
        },
        (error) => {
          openSnackBar({ message: error.response.data.message, type: "error" });
        }
      );
  }, []);

  handleFilterPatientChange = (newValue) => {
    setFilterPatient(newValue);
  };

  handleFilterDoctorChange = (newValue) => {
    setFilterDoctor(newValue);
  };

  toogleAddingPatientStatus = () => {
    setAddingPatient(!addingPatient);
  };

  toogleAddingDoctorStatus = () => {
    setAddingDoctor(!addingDoctor);
  };

  addPatient = (newPatient) => {
    let newPatientsList = patients;
    newPatientsList.unshift(newPatient);
    setPatients(newPatientsList);
    toogleAddingPatientStatus();
  };

  addDoctor = (newDoctor) => {
    let newDoctorsList = doctors;
    newDoctorsList.unshift(newDoctor);
    setDoctors(newDoctorsList);
    toogleAddingDoctorStatus();
  };

  render = () => {
    const { classes } = this.props;

    if (addingPatient) {
      addingPatient = (
        <AddPatientForm
          addPatient={addPatient}
          cancel={toogleAddingPatientStatus}
        />
      );
    } else {
      addingPatient = (
        <Box className={classes.box}>
          <Button
            className={"MuiButton-Full"}
            onClick={toogleAddingPatientStatus}
          >
            Add Patient
          </Button>
        </Box>
      );
    }
    return (
      <div>
        <Container
          maxWidth="sm"
          component="main"
          className={"MuiContainer--01"}
        >
          <Typography
            component="h1"
            variant="h2"
            align="center"
            color="textPrimary"
            gutterBottom
          >
            Patients
          </Typography>
          <Typography
            variant="h5"
            align="center"
            color="textSecondary"
            component="p"
          >
            List of Patients
          </Typography>
          <SearchBoxPatients
            inputValue={filterPatient}
            onFilterChange={handleFilterPatientChange}
          />
          {addingPatient}
          <PatientList
            patients={patients ? patients : []}
            filterPatient={filterPatient}
          />
        </Container>
        <Container
          maxWidth="sm"
          component="main"
          className={"MuiContainer--01"}
        >
          <Typography
            component="h1"
            variant="h2"
            align="center"
            color="textPrimary"
            gutterBottom
          >
            Doctors
          </Typography>
          <Typography
            variant="h5"
            align="center"
            color="textSecondary"
            component="p"
          >
            List of Doctors
          </Typography>
          <SearchBoxDoctors
            inputValue={filterDoctor}
            onFilterChange={handleFilterDoctorChange}
          />
          {addingDoctor}
          <PatientList
            doctors={doctors ? doctors : []}
            filterDoctor={filterDoctor}
          />
        </Container>
      </div>
    );
  };
}
