import { makeStyles } from "@material-ui/core";
import Button from "@material-ui/core/Button";
import Container from "@material-ui/core/Container";
import FormControl from "@material-ui/core/FormControl";
import Grid from "@material-ui/core/Grid";
import Input from "@material-ui/core/Input";
import InputLabel from "@material-ui/core/InputLabel";
import Paper from "@material-ui/core/Paper";
import Typography from "@material-ui/core/Typography";
import Axios from 'axios';
import React, { useState, useEffect } from "react";
import { openSnackBar } from "../snackBar/SnackBar";
// import sessionStorage from '/home/vishal/BITS Pilani/Sem2/NS/Project/Work/Work/NFTY/front-end/src/components/login/LoginAdmin.js';
// import UserProfile from '../login/SessionDetails';

import Web3 from 'web3'
import { TODO_LIST_ABI, TODO_LIST_ADDRESS } from '././config'

const web3 = new Web3(Web3.givenProvider);

const HelloContract = new web3.eth.Contract(TODO_LIST_ABI, TODO_LIST_ADDRESS);


const useStyles = makeStyles((theme) => ({
  paper: {
    padding: theme.spacing(4),
  },
}));

export default function CreatePage(props) {
  const [selectedFile, setSelectedFile] = useState();
  const [isSelected, setIsSelected] = useState(false);

  const changeHandler = (event) => {
    setSelectedFile(event.target.files[0]);
    setIsSelected(true);
  };

  const classes = useStyles();

  // const updateReview = (movie) => {
  //   Axios.put(`http://localhost:3001/api/update${sessionStorage.getItem("UserId")}`, {
  //   }).then(() => {
  //     alert("Successful update");
  //   });
  // }

  function sendtoBlockchain() {
    alert('Hello!');
  }

  return (
    <Container maxWidth="sm" component="main" className={"MuiContainer--01"}>
      <Paper className={classes.paper}>
        {/* <form onSubmit={handleSubmit}> */}
        <form action={`http://localhost:3001/api/createTokenImage${sessionStorage.getItem("UserId")}`} enctype="multipart/form-data" method="POST"> 

          <Grid container spacing={3}>
            <Grid item xs={12}>
              <Typography
                component="h1"
                variant="h3"
                align="center"
                color="textPrimary"
                gutterBottom
              >
                Create and Mint NFT
                        </Typography>
            </Grid>

            <Grid item xs={12}>
              <FormControl fullWidth required>
                <InputLabel htmlFor="title-input">Title</InputLabel>
                <Input
                  id="title-input"
                  // value={title}
                  name="title"
                  required
                />
              </FormControl>
            </Grid>
            <Grid item xs={12}>
              <FormControl fullWidth required>
                <InputLabel htmlFor="description-input">Description</InputLabel>
                <Input
                  id="description-input"
                  // value={description}
                  name="description"
                  required
                />
              </FormControl>
            </Grid>
            <Grid item xs={12}>
              <FormControl fullWidth required>
                <InputLabel htmlFor="price-input">Price</InputLabel>
                <Input
                  id="price-input"
                  name="price"
                  // value={price}
                  required
                />
              </FormControl>
            </Grid>
            <Grid item xs={12}>
              <FormControl fullWidth required>
                <InputLabel htmlFor="copies-count-input">Number of Copies</InputLabel>
                <Input
                  id="copies-count-input"
                  // value={copiesCount}
                  name="copiesCount"
                  type="text"
                  required
                />
              </FormControl>
            </Grid>
            <Grid item xs={12}>
              <FormControl fullWidth required>
                <InputLabel htmlFor="royalties-input">
                  Royalties
                          </InputLabel>
                <Input
                  id="royalties-input"
                  // value={royalties}
                  name="royalties"
                  type="text"
                  required
                />
              </FormControl>
            </Grid>
            <Grid item xs={12}>
              {/* <form runat="server"> */}
              <input type="file" id="pic" name="pic" />
              {isSelected ? (
                <div>
                  <p>Filename: {selectedFile.name}</p>
                  <p>Filetype: {selectedFile.type}</p>
                  <p>Size in bytes: {selectedFile.size}</p>
                </div>
              ) : (
                <p>Select a file to show details</p>
              )}
              {/* </form> */}
            </Grid>
            <Grid item xs={12}>
              <Button
                type="submit"
                className={"MuiButton-Full"}
                variant="contained"
                size="large"
                color="primary"
                onClick={sendtoBlockchain}
              >
                Create Token
                        </Button>
            </Grid>
          </Grid>
        </form>
      </Paper>
    </Container>
  );
}
