import { makeStyles, withStyles } from "@material-ui/core";
import Box from "@material-ui/core/Box";
import Button from "@material-ui/core/Button";
import Card from "@material-ui/core/Card";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import Typography from "@material-ui/core/Typography";
import axios from "axios";
import React, { useEffect, useState } from "react";

const styles = (theme) => ({
  card: {
    minWidth: 275,
    margin: theme.spacing(1),
  },
});

const useStyles = makeStyles((theme) => ({
  paper: {
    padding: theme.spacing(4),
  },
}));

function PatientCard(props) {
  const path = props.location.pathname;
  const PID = path.slice(path.lastIndexOf("/")).slice(1);
  const [patientData, setPatientData] = useState({});
  const [isExpanded, setisExpanded] = useState(false);

  useEffect(() => {
    axios
      .get(`http://localhost:3001/patient/${PID}`, {
        headers: {
          "x-access-token": localStorage.getItem("jwtToken"),
        },
      })
      .then((result) => {
        setPatientData(result.data[0]);
      });
  }, [PID]);

  return (
    <Card>
      <CardContent>
        <Box mb={1}>
          <Typography color="textSecondary" gutterBottom>
            PID {patientData.PID}
          </Typography>
          <Typography variant="h5" component="h2">
            {patientData.FirstName} {patientData.LastName}
          </Typography>
          <Typography color="textSecondary">Age: {patientData.Age}</Typography>
          <Typography color="textSecondary">
            Address: {patientData.Address}
          </Typography>
        </Box>
        {isExpanded ? (
          <Typography color="textSecondary">EHR: {patientData.EHR}</Typography>
        ) : null}
      </CardContent>
      <CardActions>
        {isExpanded ? (
          <Button size="small" onClick={() => setisExpanded(false)}>
            Shrink
          </Button>
        ) : (
          <Button size="small" onClick={() => setisExpanded(true)}>
            Expand
          </Button>
        )}
      </CardActions>
    </Card>
  );
}

export default withStyles(styles, { withTheme: true })(PatientCard);
