import React, { useState, useEffect } from 'react';

import './App.css';
import Axios from 'axios';
// import Web3 from 'web3';

import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';

const useStyles = makeStyles({
    root: {
        maxWidth: 345,
    },
    media: {
        height: 140,
    },
});

const ItemsPage = () => {

    const classes = useStyles();

    const [movieReviewList, setReviewList] = useState([])

    useEffect(() => {
        Axios.get('http://localhost:3001/api/get').then((response) => {
            setReviewList(response.data);
        })
    }, [])

    return (
        <div>
            <h1>Welcome to Items Page</h1>



            {movieReviewList.map((val) => {



                return (


                    <div class="card">
                        <div class="card-image"><img src={'http://localhost:3001/img/' + val.userid} alt="hey" id="img"></img></div>
                        <div class="card-text">
                            <span class="date">4 days ago</span>
                            <h2>{val.user_name}</h2>
                            <h2>{val.price}</h2>
                        </div>
                        <div class="card-stats">
                            <div class="stat">
                                <div class="value">4<sup>m</sup></div>
                                <div class="type">read</div>
                            </div>
                            <div class="stat border">
                                <div class="value">5123</div>
                                <div class="type">views</div>
                            </div>
                            <div class="stat">
                                <div class="value">32</div>
                                <div class="type">comments</div>
                            </div>
                        </div>
                    </div>



                    // <div className="card">
                    //     <h1>{val.movie_name}</h1>
                    //     <p>{val.movie_review}</p>

                    //     <img src={'http://localhost:3001/img/' + val.userid} alt="hey" id="img"></img>

                    //     <button onClick={() => { deleteReview(val.movie_name) }}>Delete</button>
                    //     <input type="text" id="updateInput" placeholder="Enter new Review" onChange={(e) =>
                    //         setNewReview(e.target.value)
                    //     } />
                    //     <button onClick={() => { updateReview(val.movie_name) }}>Update</button>

                    // </div>



                )


            })}
        </div>

    );
};

export default ItemsPage;
