import React, { useState, useEffect } from 'react';

import './App.css';
import Axios from 'axios';
// import Web3 from 'web3';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
} from "react-router-dom";


import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import Header from './Token'
import Homepage from './Homepage'


const useStyles = makeStyles({
    root: {
        maxWidth: 345,
    },
    media: {
        height: 140,
    },
});


function ItemsPage() {
    const [value, setValue] = useState(null)
    const [componentToRender, setComponentToRender] = useState(null)
    const [movieReviewList, setReviewList] = useState([])

    const handleEvent = (button) => {
        setValue(button);
        setComponentToRender(value);
        // sessionStorage.setItem('userImage', button);
        console.log(button)
    };

    useEffect(() =>{

        Axios.get(`http://localhost:3001/api/get${sessionStorage.getItem("UserId")}`).then((response) =>{
        setReviewList(response.data);
      }) 
    }, [])

    const renderComponent = () => {
        switch (componentToRender) {
            case 1:
                return <Homepage />;
            default://replaced 'else' with 'default'
                return (<div>
                    <h1>Welcome to Items Page</h1>



                    {movieReviewList.map((val) => {

                        return (
                            <div class="card">
                                <div class="card-image">
                                        <img src={'http://localhost:3001/img/' + val.userid} alt="hey" id="img" onClick={() => handleEvent(1)}>
                                        </img>
                                </div>
                                <div class="card-text">
                                    <span class="date">4 days ago</span>
                                    <h2>Owner : {val.user_name}</h2>
                                    <h2>Price : {val.price} eth</h2>
                                </div>
                                <div class="card-stats">
                                    <div class="stat">
                                        <div class="value">4<sup>m</sup></div>
                                        <div class="type">read</div>
                                    </div>
                                    <div class="stat border">
                                        <div class="value">5123</div>
                                        <div class="type">views</div>
                                    </div>
                                    <div class="stat">
                                        <div class="value">32</div>
                                        <div class="type">comments</div>
                                    </div>
                                </div>
                            </div>


                        )


                    })}
                </div>);
        }
    }

    return (

        <div>
            {renderComponent()}
        </div>

    )
}

export default ItemsPage;
