import { makeStyles } from "@material-ui/core";
import Button from "@material-ui/core/Button";
import Container from "@material-ui/core/Container";
import FormControl from "@material-ui/core/FormControl";
import Grid from "@material-ui/core/Grid";
import Input from "@material-ui/core/Input";
import InputLabel from "@material-ui/core/InputLabel";
import Paper from "@material-ui/core/Paper";
import Typography from "@material-ui/core/Typography";
import axios from "axios";
import React, { useState, useEffect } from "react";
import { openSnackBar } from "../snackBar/SnackBar";
// import sessionStorage from '/home/vishal/BITS Pilani/Sem2/NS/Project/Work/Work/NFTY/front-end/src/components/login/LoginAdmin.js';
// import UserProfile from '../login/SessionDetails';

const useStyles = makeStyles((theme) => ({
  paper: {
    padding: theme.spacing(4),
  },
}));

export default function CreatePage(props) {
  const [selectedFile, setSelectedFile] = useState();
  const [isSelected, setIsSelected] = useState(false);

  const changeHandler = (event) => {
    setSelectedFile(event.target.files[0]);
    setIsSelected(true);
  };

  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  const [copiesCount, setCopiesCount] = useState("");
  const [royalties, setRoyalties] = useState("");
  const [tokenImage, setTokenImage] = useState("")

  // var userName = UserProfile.getName();
  var date = Date().toLocaleString()
  console.log(date);
  const validateForm = () => {
    let errors = [];
    if (title == "" || description == "" || price == "" || copiesCount == "" || royalties == "") {
      errors.push("Please fill all the fields");
      console.log("Please fill all the fields");
    }
    return errors;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const errors = validateForm();
    if (errors.length) {
      errors.forEach((error) => {
        openSnackBar({ message: error, type: "error" });
      });
    } else {
      axios
        .post(`http://localhost:3001/api/createTokenImage${sessionStorage.getItem("UserId")}`, {
          title,
          description,
          price,
          copiesCount,
          royalties,
          tokenImage,
          date
        })
        .then((res) => {
          console.log(res);
          if (res.status === 200) {
            openSnackBar({ message: res.data.message, type: "success" });
            props.history.push("/login");
            return;
          } else {
            openSnackBar({ message: "Authentication Error", type: "error" });
          }
        })
        .catch((error) => {
          console.log(error);
          openSnackBar({ message: "errorss", type: "error" });
        });
    }
  };

  const classes = useStyles();
  const [movieName, setMovieName] = useState("")
  const [movieReview, setReview] = useState("")
  const [movieReviewList, setReviewList] = useState([])
  const [movieImage, setmovieImage] = useState("")

  const [newReview, setNewReview] = useState("")

  return (
    <Container maxWidth="sm" component="main" className={"MuiContainer--01"}>
      <Paper className={classes.paper}>
        {/* <form onSubmit={handleSubmit}> */}
        <div className="App">

          <h1>MEDICAL DATA APPLICATION</h1>




          <div className="form">

            <form action={"http://localhost:3007/api/upload"} enctype="multipart/form-data" method="POST">

              <label>Document Name</label>

              <input type="text" name="movieName" placeholder="Enter document name" onChange={(e) =>
                setMovieName(e.target.value)
              } />


              <label>Description</label>
              <input type="text" name="movieReview" placeholder="Enter description" onChange={(e) =>
                setReview(e.target.value)
              } />

              <input type="file" id="pic" name="pic" onChange={(e) =>
                setmovieImage(e.target.value)
              } />

              <button type="submit" >Submit</button>


            </form>
          </div>
        </div>

      </Paper>
    </Container>
  );
}
