import React from "react";

export default function PatientList(props) {
  const doctors = props.doctors;
  const filter = props.filter;
  //First filter the patients by the text
  console.log(doctors);
  const listOfDoctors = doctors
    .filter(function (doctor) {
      return (
        doctor.PID.includes(filter) ||
        `${doctor.firstName} ${doctor.lastName}`.includes(filter) ||
        `${doctor.lastName} ${doctor.firstName}`.includes(filter)
      );
    })
    .map((doctor) => (
      <li key={doctor.PID}>
        <Card>
          <CardContent>
            <Box mb={1}>
              <Typography color="textSecondary" gutterBottom>
                DID {doctor.DID}
              </Typography>
              <Typography variant="h5" component="h2">
                {doctor.FirstName} {doctor.LastName}
              </Typography>
              <Typography color="textSecondary">
                Specialization: {doctor.Specialization}
              </Typography>
              <Typography color="textSecondary">
                Address: {doctor.Address}
              </Typography>
            </Box>
            <Button
              size="small"
              onClick={() => onRemoveDoctor(doctor.DID, PID)}
            >
              Remove
            </Button>
          </CardContent>
        </Card>
      </li>
    ));

  return <ul>{listOfDoctors}</ul>;
}
