import { makeStyles, withStyles } from "@material-ui/core";
import Box from "@material-ui/core/Box";
import Button from "@material-ui/core/Button";
import Card from "@material-ui/core/Card";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import Typography from "@material-ui/core/Typography";
import axios from "axios";
import React, { useEffect, useState } from "react";

const styles = (theme) => ({
  card: {
    minWidth: 275,
    margin: theme.spacing(1),
  },
});

const useStyles = makeStyles((theme) => ({
  paper: {
    padding: theme.spacing(4),
  },
}));

function DoctorCard(props) {
  const path = props.location.pathname;
  const DID = path.slice(path.lastIndexOf("/")).slice(1);
  const [doctorData, setDoctorData] = useState({});
  const [isExpanded, setisExpanded] = useState(false);
  const [patientData, setPatientData] = useState([]);
  const classes = useStyles();

  useEffect(() => {
    let patId;
    axios
      .get(`http://localhost:3001/doctor/${DID}`, {
        headers: {
          "x-access-token": localStorage.getItem("jwtToken"),
        },
      })
      .then((result) => {
        setDoctorData(result.data[0]);
      });
    axios
      .get(`http://localhost:3001/doctorRelatedToPatient/${DID}`, {
        headers: {
          "x-access-token": localStorage.getItem("jwtToken"),
        },
      })
      .then((result) => {
        patId = result.data;
        let patData = [];
        for (let i = 0; i < patId.length; i++) {
          axios
            .get(`http://localhost:3001/patient/${patId[i].PID}`, {
              headers: {
                "x-access-token": localStorage.getItem("jwtToken"),
              },
            })
            .then((result) => {
              patData.push(result.data[0].EHR);
              console.log(result.data.EHR);
            });
        }
      });
  }, [DID]);

  return (
    <div>
      <Card>
        <CardContent>
          <Box mb={1}>
            <Typography color="textSecondary" gutterBottom>
              DID {doctorData.DID}
            </Typography>
            <Typography variant="h5" component="h2">
              {doctorData.FirstName} {doctorData.LastName}
            </Typography>
            <Typography color="textSecondary">
              Specialization: {doctorData.Specialization}
            </Typography>
            <Typography color="textSecondary">
              Address: {doctorData.Address}
            </Typography>
          </Box>
          {isExpanded ? null : null}
        </CardContent>
        <CardActions>
          {isExpanded ? (
            <Button size="small" onClick={() => setisExpanded(false)}>
              Shrink
            </Button>
          ) : (
            <Button size="small" onClick={() => setisExpanded(true)}>
              Expand
            </Button>
          )}
        </CardActions>
      </Card>
      <Card></Card>
    </div>
  );
}

export default withStyles(styles, { withTheme: true })(DoctorCard);
